INSERT INTO `charactersheet`.`damagetype` values (1,"Acid","The corrosive spray of a black dragon’s breath and the dissolving enzymes secreted by a black pudding deal acid damage.");
INSERT INTO `charactersheet`.`damagetype` values (2,"Bludgeoning","Blunt force attacks—hammers, falling, constriction, and the like—deal bludgeoning damage.");
INSERT INTO `charactersheet`.`damagetype` values (3,"Cold","The infernal chill radiating from an ice devil's spear and the frigid blast o f a white dragon's breath deal cold damage.");
INSERT INTO `charactersheet`.`damagetype` values (4,"Fire","Red dragons breathe fire, and many spells conjure flames to deal fire damage.");
INSERT INTO `charactersheet`.`damagetype` values (5,"Force","Force is pure magical energy focused into a damaging form. Most effects that deal force damage are spells, including magic missile and spiritual weapon.");
INSERT INTO `charactersheet`.`damagetype` values (6,"Lightning","A lightning boltspell and a blue dragon's breath deal lightning damage.");
INSERT INTO `charactersheet`.`damagetype` values (7,"Necrotic.","Necrotic damage, dealt by certain undead and a spell such as chill touch,withers matter and even the soul.");
INSERT INTO `charactersheet`.`damagetype` values (8,"Piercing","Puncturing and impaling attacks, including spears and m onsters' bites, deal piercing damage.");
INSERT INTO `charactersheet`.`damagetype` values (9,"Poison","Venomous stings and the toxic gas o f a green dragon's breath deal poison damage.");
INSERT INTO `charactersheet`.`damagetype` values (10,"Psychic","Mental abilities such as a mind flayer's psionic blast deal psychic damage.");
INSERT INTO `charactersheet`.`damagetype` values (11,"Radiant","Radiant damage, dealt by a cleric's flame strike spell or an angel's smiting weapon, sears the flesh like fire and overloads the spirit with power.");
INSERT INTO `charactersheet`.`damagetype` values (12,"Slashing","Swords, axes, and monsters' claws deal slashing damage.");
INSERT INTO `charactersheet`.`damagetype` values (13,"Thunder","A concussive burst of sound, such as the effect o f the thunderwavespell, deals thunder damage.");
INSERT INTO `charactersheet`.`damagetype` values (14,"Healing","A wave of healing energy that restores vitality.");
INSERT INTO `charactersheet`.`damagetype` values (15, "Elemental", "Player may choose acid, cold, fire, lightning, or thunder.");
